	@Override
	public void add(Product product) {
		if(product.getFirstName().length() < 2)  {
			System.out.println("Ýsminizi hatalý girdiniz!!");
			return;
		}
		else if(product.getLastName().length() < 2){
			System.out.println("Soyisiminizi hatalý girdiniz!!");
			return;
		}
		else if(!ptr.matcher(product.getePosta()).matches()) {
			System.out.println("Mail adresinizi hatalý girdiniz!!");
			return;
		}
		else if(product.getPassword() < 6) {
			System.out.println("Þifrenizi hatalý girdiniz!!");
			return;
		}
		else{
		
			System.out.println("Kullanýcý baþarý ile kayýt oldu : " + product.getFirstName());
		}

	}

