	public OAuthAdapterManager() {
	}  
	

	@Override
	public void signUp(GoogleOAuthManager googleOAuthManager) {
			if(googleOAuthManager.getFirstName().length() < 2)  {
				System.out.println("Ýsminizi hatalý girdiniz!!");
				return;
			}
			else if(googleOAuthManager.getLastName().length() < 2){
				System.out.println("Soyisiminizi hatalý girdiniz!!");
				return;
			}
			else if(!ptr.matcher(googleOAuthManager.getePosta()).matches()) {
				System.out.println("Mail adresinizi hatalý girdiniz!!");
				return;
			}
			else if(googleOAuthManager.getPassword() < 6) {
				System.out.println("Þifrenizi hatalý girdiniz!!");
				return;
			}
			else{
				
				System.out.println("Kullanýcý baþarý ile kayýt oldu : " + googleOAuthManager.getFirstName());
			}

		
	}

